import { InputBox } from "./InputBox";
import { StyleCategory } from "./StyleCategory";
import { Configuration, OpenAIApi } from "openai";
import { useState, useEffect } from "react";

const configuration = new Configuration({
  apiKey: process.env.REACT_APP_API_KEY,
});

const openai = new OpenAIApi(configuration);
const logo = require("./apina.png");
const logo2 = require("./apina2.png");
const logo3 = require("./apina3.png");
const logo4 = require("./apina4.png");
const logo5 = require("./apina5.png");

function App() {
  const [userPrompt, setUserPromnt] = useState("");
  let number = 1;
  let size = "256x256";
  const [imageUrl, setimageUrl] = useState("");
  const [imageStyle, setimageStyle] = useState("");

  const generateImage = async () => {
    const imageParameters = {
      prompt: userPrompt + "," + imageStyle,
      n: parseInt(number),
      size: size,
    };
    const response = await openai.createImage(imageParameters);
    const urlData = response.data.data[0].url;
    setimageUrl(urlData);
  };
  const pencilDrawing = () => {
    setimageStyle(" pencil drawing");
  };
  const watercolor = () => {
    setimageStyle(" watercolor painting");
  };
  const NHL = () => {
    setimageStyle(" NHL team emblem design");
  };
  const photograph = () => {
    setimageStyle(" photograph");
  };
  const popArt = () => {
    setimageStyle(" pop art");
  };
  const noStyle = () => {
    setimageStyle("");
  };

  return (
    <main className="App">
      <p>Image generator 2000</p>

      <p className="tutorialText">
        Type what type of image you want.<br></br>
        You can add "," end of your Description and after that give some styles
        for your image.<br></br>
        You can also choose from our preset styles by clicking one of those.
        <br></br>
        Image generation can take some time so be patient.
      </p>
      {imageUrl && <img src={imageUrl} className="image" alt="ai image" />}
      <InputBox label={"Description"} setAttribute={setUserPromnt} />
      <div className="image-input-pair">
        <button className="button">
          <img
            src={logo3}
            className="image"
            alt="my image"
            onClick={pencilDrawing}
          />
        </button>
        <button className="button">
          <img
            src={logo}
            className="image"
            alt="my image"
            onClick={watercolor}
          />
        </button>
        <button className="button">
          <img
            src={logo2}
            className="image"
            alt="my image"
            onClick={photograph}
          />
        </button>
        <button className="button">
          <img src={logo4} className="image" alt="my image" onClick={popArt} />
        </button>
        <button className="button">
          <img src={logo5} className="image" alt="my image" onClick={NHL} />
        </button>
        <button className="button" ><div className="whiteBox" onClick={noStyle} /></button>
      </div>
      <p>Your style is now: {imageStyle}</p>
      <button className="main-button" onClick={generateImage}>
        Generate
      </button>
    </main>
  );
}

export default App;
